import React from 'react';

export const Header = () => {
  const headerStyle = {
    backgroundColor: '#00bfa5',  
    color: 'black',              
    textAlign: 'center',         
    padding: '20px 0',           
    fontSize: '24px',            
    fontWeight: 'bold',          
    width: '100%',               
    boxShadow: '0 1px 3px rgba(0, 0, 0, 0.12), 0 1px 2px rgba(0, 0, 0, 0.24)'  // Box shadow for depth
  };

  return (
    <header style={headerStyle}>
      Expense Tracker
    </header>
  );
}
